const items = $input.all();
const results = [];

for (const item of items) {
  const email = item.json;

  const priorityMap = {
    'URGENTE': '1',
    'ALTA': '2',
    'NORMAL': '3',
    'BAIXA': '4'
  };

  const topicMap = {
    'Financeiro': 'Financeiro',
    'TI': 'Suporte TI',
    'RH': 'Recursos Humanos',
    'Admin': 'Administrativo'
  };

  const ticketData = {
    name: 'Administrativo',
    email: email.from_email,
    subject: `[ADMIN] ${email.subject || '(Sem assunto)'}`,
    message: `Departamento: ${email.department_name}
Subcategoria: ${email.subcategory}

Resumo:
${email.summary || ''}

---
Email Original:
${email.body_text}

---
Prioridade: ${email.priority}`,
    priority: priorityMap[email.priority] || '3',
    topicId: topicMap[email.department_name] || 'Administrativo',
    autorespond: false,
    alert: email.priority === 'URGENTE'
  };

  results.push({
    json: {
      ...email,
      osticket_payload: ticketData
    }
  });
}

return results;

const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const response = item.json;
    const ticketNumber = response.ticket_number || response.id || 'UNKNOWN';
    const emailData = $('Get Admin Emails Needing Tickets').item.json;

    results.push({
      json: {
        email_id: emailData.email_id,
        classification_id: emailData.classification_id,
        ticket_number: ticketNumber,
        ticket_source: 'osticket',
        ticket_status: 'open',
        department_id: emailData.department_id,
        priority: emailData.priority,
        sla_deadline: emailData.response_time_hours ? 
          new Date(Date.now() + (emailData.response_time_hours * 60 * 60 * 1000)).toISOString() : null,
        created_successfully: true
      }
    });
  } catch (error) {
    results.push({
      json: {
        email_id: $('Get Admin Emails Needing Tickets').item.json.email_id,
        error: error.message,
        created_successfully: false
      }
    });
  }
}

return results;
